
# MODULE BOUNDARY â€” Robust Feedback Layer (appended for demo)
from __future__ import annotations
from dataclasses import dataclass, field
from typing import Dict, List, Set, Tuple, Hashable, Optional


Token = Hashable
ModeId = Hashable


@dataclass
class FeedbackLayerDemo:
	modes: List[ModeId]
	beta: Dict[Tuple[ModeId, Token], float] = field(default_factory=dict)
	learning_rate: float = 0.1
	clamp_min: float = -2.0
	clamp_max: float = 2.0
	verbose: bool = False

	def score_modes(self, active_tokens: Set[Token], weights: Dict[Token, float]) -> Dict[ModeId, float]:
		scores: Dict[ModeId, float] = {m: 0.0 for m in self.modes}
		for tok in active_tokens:
			wt = float(weights.get(tok, 0.0))
			if wt <= 0.0:
				if self.verbose:
					print(f"[warn] token '{tok}' has zero weight; ignored")
				continue
			found_any = False
			for m in self.modes:
				key = (m, tok)
				b = self.beta.get(key)
				if b is not None:
					scores[m] += b * wt
					found_any = True
			if not found_any and self.verbose:
				print(f"[warn] token '{tok}' has no associations in Î²; check configuration")
		return scores

	def select_mode(self, active_tokens: Set[Token], weights: Dict[Token, float]) -> ModeId:
		scores = self.score_modes(active_tokens, weights)
		# deterministic tie-break: earliest mode in list
		best_mode = self.modes[0]
		best_score = scores.get(best_mode, float('-inf'))
		for m in self.modes[1:]:
			s = scores.get(m, float('-inf'))
			if s > best_score:
				best_mode, best_score = m, s
		return best_mode

	def apply_feedback(
		self,
		mode_used: ModeId,
		engraved_tokens: Set[Token],
		reward: float,
	) -> None:
		if reward == 0.0:
			return
		for tok in engraved_tokens:
			key = (mode_used, tok)
			self.beta[key] = self.beta.get(key, 0.0) + self.learning_rate * reward
		# clamp Î² within configured bounds
		for k in list(self.beta.keys()):
			v = self.beta[k]
			if v < self.clamp_min:
				self.beta[k] = self.clamp_min
			elif v > self.clamp_max:
				self.beta[k] = self.clamp_max

	def snapshot_beta(self) -> Dict[str, float]:
		return {f"{m}|{t}": v for (m, t), v in self.beta.items()}


def _normalized_weights(tokens: List[Token]) -> Dict[Token, float]:
	# simple EWMA-free placeholder: uniform weights
	if not tokens:
		return {}
	w = 1.0 / float(len(tokens))
	return {tok: w for tok in tokens}


def _demo_run() -> None:
	# minimal demo: three modes, three tokens
	modes = ["neutral", "supportive", "directive"]
	demo = FeedbackLayerDemo(modes=modes, verbose=True)

	session_tokens: List[Token] = ["overload", "bf_play", "engineering"]
	weights = _normalized_weights(session_tokens)
	active = set(session_tokens)

	# seed a few associations
	demo.beta[("supportive", "overload")] = 0.8
	demo.beta[("neutral", "engineering")] = 0.6
	demo.beta[("directive", "engineering")] = 0.5

	chosen = demo.select_mode(active_tokens=active, weights=weights)
	print(f"chosen mode: {chosen}")

	# apply positive feedback for the chosen pairing
	demo.apply_feedback(mode_used=chosen, engraved_tokens=active, reward=+1.0)

	print("Î² snapshot:")
	for k, v in sorted(demo.snapshot_beta().items()):
		print(f"  {k}: {v:.3f}")


if __name__ == "__main__":
	import sys
	if "--demo" in sys.argv:
		_demo_run()
